create table if not exists outstanding_balances ( id uuid primary key default uuid_generate_v4(), group_id uuid not null references groups(id) on delete cascade, user_id uuid not null references users(id) on delete cascade, amount numeric(12,2) not null, reason text, created_at timestamptz default now(), status text not null default 'open' );
create table if not exists notifications ( id uuid primary key default uuid_generate_v4(), user_id uuid not null references users(id) on delete cascade, title text not null, body text not null, meta jsonb, created_at timestamptz default now(), is_read boolean default false );
create table if not exists audit_logs ( id uuid primary key default uuid_generate_v4(), actor_id uuid references users(id), group_id uuid references groups(id), action text not null, details jsonb, created_at timestamptz default now() );
